import React from 'react';
import { useState } from 'react';
import MoviesList from './components/MoviesList';
import './App.css';

function App() {
  const [movies, setMovies] = useState([])
  const [isLoading, setIsLoading] = useState(false)
  const [error, setError] = useState(null)
  const [cancel,setCancel] = useState(false)
  const fetchMoviesHandler = async ()=>{
    if (!cancel){


    setError(null)
    setIsLoading(true)
    try{

      const rawData = await fetch('https://swapi.dev/api/film/') 
    if (!rawData.ok){
      setError("Something went wrong!")
      throw new Error('Something went wrong! Retrying...')
    }
    const response = await rawData.json()
    const myMoviesList = response.results.map(item=> {
      return {
        title : item.title,
        releaseDate : item.release_date,
        openingText : item.opening_crawl
      }
    })
    setMovies(myMoviesList)

    }
    catch(err){
      setError(err.message)
      setTimeout(()=>{ fetchMoviesHandler() },3000)
    }
    setIsLoading(false)
  }
  else{
    setCancel(false)
  }
  }

  const cancelHandler = ()=>{
    setCancel(true)
  }

  return (
    <React.Fragment>

      <section>
        <button onClick={fetchMoviesHandler}>Fetch Movies</button>
      </section>
      <section>
       {!isLoading && movies.length>0 && <MoviesList movies={movies} />}
        {isLoading && <p>Loading...</p>}
        {!isLoading && movies.length===0 && !error && <p>No movies found</p>}
        {error&& <p>{error}</p>}
        {error&& <button className='button' onClick={cancelHandler}>Cancel</button>}

      </section>
    </React.Fragment>
  );
}

export default App;
